package chap02;

public class Ex3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 100;
		System.out.println("num = " + num);
		
		num = 123;
		System.out.println("num = " + num);
		
		final double PI = 3.141592;
		System.out.println("PI = "+ PI);
		// final 상수만들어줌
		
		final double PI1;
		PI1 = 3.14;
		System.out.println("PI1 = "+ PI1);
		
	}

}
